pageContext.rootQuery:
{
  prismic {
    allHomepages {
      edges {
        node {
          hero_title
          hero_button_text
          hero_button_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }
          content
          about_title
          about_bio
          about_links {
            about_link
          }
        }
      }
    }
    allProjects(sortBy:project_post_date_DESC first:3) {
      edges {
        node {
          project_title
          project_preview_description
          project_preview_thumbnail
          project_stacks {
            project_stack
          }
          github_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }
          demo_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }
          project_post_date
          _meta {
            uid
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
    }
  }
}
