pageContext.rootQuery:
query ProjectQuery($uid: String) {
  prismic {
    allProjects(uid: $uid) {
      edges {
        node {
          project_title
          project_preview_description
          project_preview_thumbnail
          project_stacks {
            project_stack
          }
          github_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }
          demo_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }

          project_post_date
          project_hero_image
          project_description
          _meta {
            uid
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
    }
  }
}
